import axios from "axios";

const api = axios.create({
  baseURL: process.env.REACT_APP_API_URL || "http://localhost:5555",
  withCredentials: true, // Ensures cookies/credentials are sent with requests
});

api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response) {
      // Stop the loop on a 404 or 401 error
      if (error.response.status === 404 || error.response.status === 401) {
        console.error("Error: ", error.response.status, error.response.data);
        // Optionally redirect or handle unauthorized access
        window.location.href = "/login"; // Example: Redirect to login on unauthorized
      }
    }
    return Promise.reject(error);
  }
);

export default api;
